import React, { Component } from "react";
import {Link} from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import logo from "../brain.png";

// todo refactor to be more responsive and take up full page
class BuildProgressMenu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            form_data: this.props.form_data
        };
    }

    // todo -- my concern: refreshing the page makes the form data disappear. user will lose progress if they refresh the window.
    //  how to get around this?

    componentWillReceiveProps(nextProps, nextContext) {
        console.log("nextprops: " + nextProps);
        console.log("nextcontext" + nextContext);
        this.setState({

        });
    }

    render() {
        return (
            <div>
                <p>{this.state.form_data}</p>
                <h5 align="left" style={{position: 'absolute', top: '25%', left: '20%'}}>Preprocessing stage is
                    complete!</h5>
                <h5 align="left" style={{position: 'absolute', top: '33%', left: '20%'}}>Sequences Processed: N/A </h5>

                <div className="btnContainer btn-group" role="group"
                     style={{position: 'absolute', top: '50%', left: '20%'}}>
                    <Link to="/">
                        <Button type="button" className="btn btn-secondary"
                                style={{borderColor: 'black', backgroundColor: '#0F5298'}}>Restart with another
                            Dataset</Button>
                    </Link>

                    <Link to="/ExploreClusters">
                        <Button type="button" className="btn btn-secondary"
                                style={{borderColor: 'black', backgroundColor: '#0F5298'}}>Explore Loaded Data</Button>
                    </Link>

                    {/* todo to send props through Link component: */}
                    {/*<Link to={{ pathname: '/', state: 'flushDeal' }}>
                        Home
                    </Link>
                    source: https://medium.com/@dcai900/how-to-refresh-data-when-using-react-router-link-396a2ddf8373
                    */}

                    <Link to="/QueryFinder">
                        <Button type="button" className="btn btn-secondary"
                                style={{borderColor: 'black', backgroundColor: '#0F5298'}}>Find Similar
                            Sequences</Button>
                    </Link>
                </div>
            </div>
        );
    }
}

export default BuildProgressMenu;
